class Hotkey{constructor(a){this.hotkeys=[],this.init(),this.add(a)}remove(a){this.hotkeys.splice(a,1)}add(a){isArray(a)?this.hotkeys=this.hotkeys.concat(a):this.hotkeys.push(a)}checkKey(a){this.hotkeys.forEach(b=>[b.ctrl&&a.ctrlKey||!b.ctrl,b.shift&&a.shiftKey||!b.shift,b.alt&&a.altKey||!b.alt,b.key==a.code||!b.key].every(a=>!0==a)&&b.func())}init(){document.addEventListener("keydown",a=>this.checkKey(a))}}